import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import Login from './components/Login';
import BookList from './components/BookList';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Inicio</Link>
            </li>
            {!isLoggedIn && (
              <li>
                <Link to="/login">Login</Link>
              </li>
            )}
            {isLoggedIn && (
              <>
                <li>
                  <Link to="/books">Mis Libros</Link>
                </li>
                <li>
                  <button onClick={() => setIsLoggedIn(false)}>Desconectar</button>
                </li>
              </>
            )}
          </ul>
        </nav>

        <Switch>
          <Route path="/login">
            <Login setIsLoggedIn={setIsLoggedIn} />
          </Route>
          <Route path="/books">
            <BookList />
          </Route>
          <Route path="/">
            <h1>Bienvenido</h1>
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
